mapper.select:
select a.*,b.name ,c.identity parent_id,c.name parent_name,t.user_name from dep_brand_category a
left join dep_equip_classify b on a.identity = b.identity
left join dep_equip_category c on b.parent_identity = c.identity
left join token_xkstaff_department t on a.user_id = t.user_id

     and a.identity = #{identity}
     and a.time = #{time}
     and a.two_dep = #{twoDep}
     and a.id = #{id}

select a.*,b.name ,c.identity parent_id,c.name parent_name,t.user_name from dep_brand_category_his a left join dep_equip_classify b on a.identity = b.identity left join dep_equip_category c on b.parent_identity = c.identity left join token_xkstaff_department t on a.user_id = t.user_id
and a.id = #{id}
order by a.time

select a.identity,sum(a.new_brand) newSum,sum(a.old_brand) oldSum  from dep_brand_category a
where a.identity = #{identity} and a.two_dep = #{twoDep}
group by a.identity
