mapper.select:
select a.*,b.name ,c.identity parent_id,c.name parent_name,t.user_name from dep_number_category a
left join dep_equip_classify b on a.identity = b.identity
left join dep_equip_category c on b.parent_identity = c.identity
left join token_xkstaff_department t on a.user_id = t.user_id

     and a.identity = #{identity}
     and a.time = #{time}
     and a.two_dep = #{twoDep}
     and a.id = #{id}

select a.*,b.name ,c.identity parent_id,c.name parent_name,t.user_name from dep_number_category_his a left join dep_equip_classify b on a.identity = b.identity left join dep_equip_category c on b.parent_identity = c.identity left join token_xkstaff_department t on a.user_id = t.user_id
and a.id = #{id}
order by a.time

select a.*,b.user_name user_name,c.user_name person_name from dep_opinion_feedback a
left join token_xkstaff_department b on a.user_id = b.user_id
left join token_xkstaff_department c on a.person_id = c.user_id

     and a.two_dep = #{twoDep}
     and a.seq_no = #{seqNo}

order by a.opinion_time desc
