mapper.update:
update weekly_demand_scheduel
demand_name = #{demandName}, host_team = #{hostTeam}, host_team_id = #{hostTeamId}, host_manager = #{hostManager}, host_manager_id = #{hostManagerId}, demand_dept = #{demandDept}, demand_dept_id = #{demandDeptId}, processing_time = #{processingTime}, planned_production_time = #{plannedProductionTime}, plan_rd_completion_time = #{planRdCompletionTime}, current_generation = #{currentGeneration}, finished_phase = #{finishedPhase}, finish_this_week = #{finishThisWeek}, risks_difficulties = #{risksDifficulties}, remarks = #{remarks}, serial_no = #{serialNo},
where id = #{id}

mapper.select:
select min(id) as id,host_team from WEEKLY_DEMAND_SCHEDUEL where 1 = 1
and host_team_id = #{hostTeamId}
group by host_team order by nlssort(host_team,'NLS_SORT = SCHINESE_PINYIN_M')
